<?php  //TODO: move $body_classes to controller function?  // Add switches to body-class (no 'hulu').  $body_classes = "oup mtype-$meta->media_type width-$meta->width theme-{$theme->name} hide-tscript hide-captions hide-settings oup-paused ";  //Experimental.  if ($req->hide_controls /*&& 'video'==$meta->media_type*/) {    $body_classes.= "ctl-overlay ";  }  // Language/locale, 'context' (ctx-), 'mode' etc.  $body_classes.= ' lang-'.$this->lang->lang_code();  $body_classes.= ' -'.$this->agent->browser_code();  $body_classes.= ' ctx-'.get_class($meta);  $body_classes.= " mode-$mode "; #(embed|popup).  $body_classes.= $debug ? 'debug ':'no-debug ';  $body_classes.= $meta->poster_url  ? 'has-poster ':'no-poster ';  $body_classes.= $meta->caption_url ? 'has-captions ':'no-captions ';  $body_classes.= isset($meta->transcript_html)&&$meta->transcript_html ? 'has-tscript ':'no-tscript '; //Was 'hide-script'  $body_classes.= isset($meta->_related_url)&&$meta->_related_url ? 'has-rel-link ':'no-rel-link ';  $body_classes.= isset($meta->_access)&&'Y'==$meta->_access['private'] ? 'is-private ':'not-private ';echo $body_classes;?>